var app=new Vue({el:"#app",data:{status:0,loading:!1,orders:[],selectedOrder:null},mounted:function(){this.getOrders()},watch:{status:function(){this.getOrders()}},methods:{getOrders:function(){this.loading=!0;axios.get("/orders?status="+this.status).then(n=>{this.orders=n.data,this.loading=!1})},selectOrder:function(n){this.loading=!0;axios.get("/orders/"+n).then(n=>{this.selectedOrder=n.data,this.loading=!1})},updateOrder:function(){this.loading=!0;axios.put("/orders/"+this.selectedOrder.id,null).then(()=>{this.loading=!1,this.exitOrder(),this.getOrders()})},exitOrder:function(){this.selectedOrder=null}},computed:{}});